import { Component, OnInit } from '@angular/core';
import { PostListItem } from '../model/post';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-posts',
  templateUrl: './posts.component.html',
})
export class PostsComponent implements OnInit {

  constructor() {

  }

  displayedColumns: string[] = ['favorite', 'title', 'author', 'comments'];

  postsDataSource: MatTableDataSource<PostListItem> = new MatTableDataSource<PostListItem>();

  ngOnInit(): void {

  }
}

<div class="row mb">
    <div class="col-xs-12">
        <button class="btn btn-primary">
            Reset
        </button> 
    </div>
</div>


<div class="row mb">
    <div class="col-xs-12">

        <table mat-table [dataSource]="postsDataSource" class="mat-elevation-z8">

            <ng-container matColumnDef="favorite">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let post">
                    // TODO: Clickable star
                    {{post.favorite}}
                </td>
            </ng-container>

            <ng-container matColumnDef="title">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let post">
                    {{post.title}}
                </td>
            </ng-container>

            <ng-container matColumnDef="author">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let post">
                    {{post.author}}
                </td>
            </ng-container>

            <ng-container matColumnDef="comments">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let post">
                    {{post.comments}}
                </td>
            </ng-container>

            <ng-container matColumnDef="actions">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let post">
                    // TODO: Delete button
                </td>
            </ng-container>

        </table>

    </div>
</div>
